//
// CLV Wishing Well
// Trust the plan.
//

pragma solidity ^0.5.16;

import './Clover.sol';


contract CLVFaucet {

    struct Info {
		Clover clv;
	}
    Info private info;

	constructor(address _CLV_address) public {
		info.clv = Clover(_CLV_address);
	}

     //get current balance of the faucet
    function currentBalance() public view returns (uint256){
        return info.clv.balanceOf(address(this));
    }

    function payin(uint256 amount) public returns (bool){
        if(amount>0){
            info.clv.transferFrom(msg.sender, address(this), amount);
        }
        return true;
    }

    function payout(uint256 amount) public returns (bool){
        if(amount>0){
            info.clv.transfer(msg.sender, amount);
        }
        return true;
    }


}
